import { Component } from "react";

import ScrollMagic from "ScrollMagic";
import "animation.gsap";
import "debug.addIndicators";
import { square, placeholder } from "./index.module.scss";

export default class Scroll extends Component {
  constructor(props) {
    super(props);
    this.controller = new ScrollMagic.Controller();
  }

  componentDidMount() {
    new ScrollMagic.Scene({
      triggerElement: "#scrollStarts",
      duration: 400, // scroll distance
      offset: 0 // start this scene after scrolling for 50px
    })
      .setTween("#myElement", {
        x: 220,
        color: "white"
      })
      .addIndicators({ name: "aaaaaaa" })
      //   .setPin("#myElement") // pins the element for the the scene's duration
      .addTo(this.controller); // assign the scene to the controller
  }

  render() {
    return (
      <>
        <div className={placeholder} />
        <div id="scrollStarts">
          Scrolling animation starts when this is in the middle ------->
        </div>
        <div style={{ height: "600px", background: "yellow" }}>
          <div
            id="wrapper"
            style={{ height: "1800px", background: "lightgreen" }}
          >
            <h1 id="myElement">Hiiiiiii</h1>
          </div>
        </div>
      </>
    );
  }
}
