  square,
  placeholder,
  que,
  mainHeader,
  scene,
  eye
} from "./index.module.scss";

export default class Scroll extends Component {
  constructor(props) {
    super(props);
    this.controller = new ScrollMagic.Controller();

    this.symbol = null;
    // reference to the animation
    this.myTween = null;
